import React, { useState } from 'react';
import './Finanzas.css';

const Finanzas = () => {
  // Estado para el formulario financiero
  const [formData, setFormData] = useState({
    concepto: '',
    monto: '',
    tipo: 'ingreso',
    fecha: ''
  });

  // Datos de prueba para la sección financiera
  const [finanzasData] = useState({
    titulo: "Dashboard Financiero",
    resumen: {
      ingresos: 12500,
      gastos: 8450,
      balance: 4050
    },
    ultimasTransacciones: [
      { id: 1, concepto: "Venta producto A", monto: 3500, tipo: "ingreso", fecha: "2023-05-15" },
      { id: 2, concepto: "Pago nómina", monto: 4200, tipo: "gasto", fecha: "2023-05-14" },
      { id: 3, concepto: "Servicios públicos", monto: 850, tipo: "gasto", fecha: "2023-05-12" },
      { id: 4, concepto: "Venta producto B", monto: 2800, tipo: "ingreso", fecha: "2023-05-10" }
    ]
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
        const response = await fetch("http://localhost:5000/api/egresos", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(formData),
        });

        if (response.ok) {
            alert("Egreso registrado con éxito");
            setFormData({ concepto: "" }); // Limpiar formulario
        } else {
            const errorData = await response.json();
            alert(errorData.error);
        }
    } catch (error) {
        console.error("Error al enviar los datos:", error);
    }
};

  return (
    <div className="finanzas-container">
      {/* Sidebar para registro financiero */}
      <div className="finanzas-sidebar">
        <h2>Egresos De La Empresa</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="concepto">Concepto:</label>
            <input
              type="text"
              id="concepto"
              name="concepto"
              value={formData.concepto}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="monto">Descripcion:</label>
            <input
              type="text"
              id="descripcion"
              name="descripcion"
              value={formData.monto}
              onChange={handleChange}
              min="0"
              step="0.01"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="monto">Monto:</label>
            <input
              type="number"
              id="monto"
              name="monto"
              value={formData.monto}
              onChange={handleChange}
              min="0"
              step="0.01"
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="fecha">Fecha:</label>
            <input
              type="date"
              id="fecha"
              name="fecha"
              value={formData.fecha}
              onChange={handleChange}
              required
            />
          </div>
          
          <button type="submit" className="submit-btn">Registrar</button>
        </form>
      </div>
      
      {/* Contenido principal de finanzas */}
      <div className="finanzas-main">
        <h1>{finanzasData.titulo}</h1>
        
        <div className="resumen-financiero">
          <div className="resumen-card ingreso">
            <h3>Ingresos</h3>
            <p>${finanzasData.resumen.ingresos.toLocaleString()}</p>
          </div>
          <div className="resumen-card gasto">
            <h3>Gastos</h3>
            <p>${finanzasData.resumen.gastos.toLocaleString()}</p>
          </div>
          <div className="resumen-card balance">
            <h3>Balance</h3>
            <p>${finanzasData.resumen.balance.toLocaleString()}</p>
          </div>
        </div>
        
        <div className="transacciones-recientes">
          <h2>Últimas Transacciones</h2>
          <table>
            <thead>
              <tr>
                <th>Concepto</th>
                <th>Monto</th>
                <th>Tipo</th>
                <th>Fecha</th>
              </tr>
            </thead>
            <tbody>
              {finanzasData.ultimasTransacciones.map((trans) => (
                <tr key={trans.id}>
                  <td>{trans.concepto}</td>
                  <td className={trans.tipo}>${trans.monto.toLocaleString()}</td>
                  <td>{trans.tipo}</td>
                  <td>{trans.fecha}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Finanzas;
